grep '\.txt' tag_counter_unicode_xml.sh | tr -s '>' | rev | cut -d'>' -f1 | rev | sed 's;instances/;;' | grep '\.instances' | sed 's/^[ ]*//' | sort | uniq | cut -d'#' -f1 | tr -d ' ' | tr '.' '\t' | cut -f1-3 > features.tab

}


